@prefix rdf:  <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix inst: <https://example.org/projectXX/> .
@prefix BIMDO: <http://www.marquette.edu/BIM_Design_Ontology#> .
@prefix BIMSO: <http://www.marquette.edu/BIM_Shared_Ontology#> .
@prefix unit: <http://qudt.org/vocab/unit/> .
@prefix qudt: <http://qudt.org/schema/qudt#> .
@prefix quantitykind: <http://qudt.org/vocab/quantitykind/> .
@prefix fc: <http://www.freeclass.eu/freeclass_v1#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

###The building element
inst:wall1 a BIMSO:B2010101 ;
#data type properties directly
	BIMDO:hasIdentity "123456" ;
	BIMDO:hasDescription "This is a wall" ;
#intermediate object properties, going to data type or other object properties
	BIMDO:hasVolume inst:propA ;
	BIMDO:hasMaterial inst:redBricksA .

###Intermediate object property going to a data type property
inst:propA a BIMDO:Volume ;
	BIMDO:hasValue "2"^^xsd:integer ; #you can probably also use rdf:value for this
	BIMDO:hasDimensionUnit unit:M3 .

###intermediate object property going to other object property or data type property
inst:redBricksA a fc:C_A1143-gen ;
	BIMDO:Color BIMDO:red ; #object property (object property)
	BIMDO:hasVolume inst:propB .
BIMDO:red a BIMDO:MaterialQualitativeProperty .

inst:propB a BIMDO:MaterialQuantitativeProperty ;
	BIMDO:hasMQUnit unit:M3 ;
	BIMDO:hasValue "0.0031"^^xsd:float .

unit:M3 qudt:hasQuantityKind quantitykind:Volume ;
	a qudt:Unit .

fc:C_A1143-gen rdfs:label "bricks of expanded clay [Generic Concept: This type of goods]"@en .

